import React, { useState } from "react";

const TodoForm = () => {
    const [input, setInput] = useState("");

    let id;
    const handleChange = (e) => {
        id = setTimeout(() => {
            setInput(e.target.value);
        }, 500)
        clearTimeout(id);
    }

    return (
        <div>
            <input 
                type="text"
                className="todo-input"
                name="todo"
                onChange={handleChange}
            />
            <span>+</span>
        </div>
    );
};

export default TodoForm;
