import React from "react";
import "../styles/Items.css"

const TodoItem = ({todo, deleteItem}) => {
    return (
        <div className={todo.isComplete ? "item" : "item-through"}>
            <div className="item" onDoubleClick={() => completeTodo}>{todo.text}</div>
            <button className="btn" onClick={() => deleteItem(todo.id)}>X</button>
        </div>
    );
};

export default TodoItem;
