import React from "react";
import "../styles/Items.css"

const TodoItem = ({todo, deleteItem, completeTodo}) => {
    return (
        <div className="items">
            <div className={todo.isComplete ? "item-through" : "item"} onDoubleClick={() => completeTodo(todo.id)}>{todo.text}</div>
            <button className={todo.isComplete} onClick={() => deleteItem(todo.id)}>X</button>
        </div>
    );
};

export default TodoItem;
