import React, {useEffect, useState} from "react";
import TodoForm from "./TodoForm";
import TodoItem from "./TodoItem";
import "../styles/FormStyles.css"

const LOCALE_STORAGE_KEY = "todoApp.todos";

const TodoList = () => {
    const [todos, setTodos] = useState([]);
    const [editId, setEditId] = useState(false);
    const [inputValue, setInputValue] = useState("");

    //Locale Storage
    useEffect(() => {
        localStorage.setItem(LOCALE_STORAGE_KEY, JSON.stringify(todos));
    }, [todos]);

    useEffect(() => {
        const storedTodos = JSON.parse(localStorage.getItem(LOCALE_STORAGE_KEY)) ;
        if (storedTodos) setTodos(storedTodos);
    }, [])


    const handleEditChange = (id, text) => {
        setEditId(id);
        setInputValue(text);
    };

    const editTodo = (id, text) => {
        let editTodos = todos.map((todo) => {
            if (todo.id === id) {
                todo.text = text;
            }
            return todo;
        });
        setTodos(editTodos);
        setEditId(false)
    };

    const addTodo = (todo) => {
        const newTodo = [todo, ...todos];

        setTodos(newTodo);

        console.log(newTodo)
    };

    const deleteItem = id => {
        let removeArr = [...todos].filter(todo => todo.id !== id);

        setTodos(removeArr)
    };

    const completeTodo = id => {
        const updatedTodos = todos.map(todo => {
            if (todo.id === id) {
                todo.isComplete = !todo.isComplete;
            }
            return todo;
        });

        setTodos(updatedTodos)
    };



    return (
        <div className="todo-form">
            <TodoForm onSubmit={addTodo} />
            {
                todos.map((todo, index) => (
                    <TodoItem 
                        key={index} 
                        todo={todo} 
                        deleteItem={deleteItem} 
                        completeTodo={completeTodo}  
                        editTodo={editTodo}
                        handleEditChange={handleEditChange}
                        editId={editId}
                        inputValue={inputValue}
                        setInputValue={setInputValue}
                    />
                ))
            }
        </div>
    );

};

export default TodoList;
