import React, { useState } from "react";
import "../styles/Items.css"

const TodoItem = ({
    todo,
    deleteItem,
    completeTodo,
    editTodo,
    handleEditChange,
    editId,
    inputValue,
    setInputValue
}) => {



    return (
        <div className="items">
            {
                editId === todo.id ? (
                    <input
                        type="text"
                        value={inputValue}
                        onChange={(e) => setInputValue(e.target.value)}
                    />
                ) : (
                    <div
                        className={todo.isComplete ? "complete" : ""}
                        onDoubleClick={() => handleEditChange}
                    />
                )
            }
        </div>
    );
};

export default TodoItem;
